import React, { useState, useEffect } from 'react';

const FloatingEmoji = () => {
  const emojis = ['❤️', '🥰', '😍', '💖', '😘', '💕', '🤗'];
  const [emoji] = useState(emojis[Math.floor(Math.random() * emojis.length)]);
  const [position, setPosition] = useState({
    x: Math.random() * window.innerWidth,
    y: Math.random() * window.innerHeight,
  });
  const [rotation, setRotation] = useState(Math.random() * 360);

  // Cambiar la velocidad inicial para que se mueva más lentamente
  const [velocity] = useState({
    x: (Math.random() - 0.5) * 1,  // Reducir la velocidad
    y: (Math.random() - 0.5) * 1,  // Reducir la velocidad
  });

  const [scale] = useState(0.8 + Math.random() * 0.4);

  useEffect(() => {
    const moveEmoji = () => {
      setPosition(prev => {
        let newX = prev.x + velocity.x;
        let newY = prev.y + velocity.y;

        // Condiciones de rebote para mantener el emoji dentro de la pantalla
        if (newX <= 0 || newX >= window.innerWidth - 60) {
          velocity.x *= -1;
        }
        if (newY <= 0 || newY >= window.innerHeight - 60) {
          velocity.y *= -1;
        }

        return { x: newX, y: newY };
      });

      // Añadir una pequeña rotación constante
      setRotation(prev => (prev + 0.5) % 360);
    };

    const animationId = requestAnimationFrame(function animate() {
      moveEmoji();
      requestAnimationFrame(animate);
    });

    return () => cancelAnimationFrame(animationId);
  }, [velocity.x, velocity.y]); // Mantener las velocidades constantes

  return (
    <div 
      className="floating-emoji"
      style={{
        position: 'absolute',
        left: `${position.x}px`,
        top: `${position.y}px`,
        transform: `rotate(${rotation}deg) scale(${scale})`,
        transition: 'transform 0.3s ease',
        zIndex: 1,
        pointerEvents: 'none',
        fontSize: '40px',
        filter: 'drop-shadow(0 0 5px rgba(255,0,0,0.3))'
      }}
    >
      {emoji}
    </div>
  );
};

export default FloatingEmoji;
